(function(){const loading=document.getElementById('loading');const timer=setTimeout(function(){loading.style.visibility='hidden';clearTimeout(timer);},4000);const toggleBtn=document.getElementById('toggle-btn'),navList=document.getElementById('nav-list');toggleBtn.addEventListener('click',function(e){e.preventDefault();navList.style.display=navList.style.display=='none'?'block':'none';},true);navList.addEventListener('click',function(){this.style.display='none';},true);window.onscroll=function(){let scrollTop=document.documentElement.scrollTop||document.body.scrollTop;const navBox=document.getElementById('nav-box');if(scrollTop>=620){navBox.style.position='fixed';navBox.style.top='10%';}else{navBox.style.position='absolute';navBox.style.top='100vh';}var navItem=document.getElementsByClassName('nav-item');function setActiveDom(dataName){const navItemArr=Array.prototype.slice.call(navItem);navItemArr.forEach(item=>{item.classList.remove('active');});navItemArr.forEach(item=>{if(item.getAttribute('data-name')==dataName){item.classList.add('active');}});}const bascInfo=document.getElementById('basic-info'),skill=document.getElementById('skill'),project=document.getElementById('project');const basicInfoArea=bascInfo.offsetTop+bascInfo.offsetHeight-200;const skillArea=skill.offsetTop+skill.offsetHeight-200;const projectArea=project.offsetTop+project.offsetHeight-800;if(scrollTop<=basicInfoArea){setActiveDom('basc-info');}else if(scrollTop>basicInfoArea&&scrollTop<=skillArea){setActiveDom('skill');}else if(scrollTop>skillArea&&scrollTop<=projectArea){setActiveDom('project');}else{setActiveDom('contact');}}}())